import '../styles/box.css';
import MyContext from '../MyContext';

class ColorBox extends React.Component {
  render() {
    return(
      <MyContext.Consumer>
        {
          ({ colors, handleClicj })
        }
        <button
          type="button"
          className="box"
          style={ { backgroundColor: 'blue' } }
        >
          Click me to change my color!
        </button>
      </MyContext.Consumer>
    )
  }
}
export default ColorBox;
