import React, { useState } from 'react';

function Forms() {
  const [name, setName] = useState('');
  const [age, setAge] = useState(0);
  const [city, setCity] = useState('');
  const [_module, setModule] = useState('');

  return (
    <div>
      <input value={name} onChange={(event) => setName(event.target.value)} type="text" name="name" placeholder="Nome completo" /><br /><br />

      <input value={age} onChange={(event) => setAge(event.target.value)} type="number" name="age" id="age"  placeholder="Idade" /><br /><br />

      <input value={city} onChange={(event) => setCity(event.target.value)} type="text"  name="city" placeholder="Cidade"/><br /><br />

      <label htmlFor="fundamentals">
        <input value="fundamentals" onChange={(event) => setModule(event.target.value)} type="radio" name="module" id="fundamentals" />
        fundamentals
      </label>

      <label htmlFor="front">
        <input value="front" onChange={(event) => setModule(event.target.value)} type="radio" name="module" id="front" />
        front
      </label>

      <label htmlFor="back">
        <input value="back" onChange={(event) => setModule(event.target.value)} type="radio" name="module" id="back" />
        back
      </label>

      <label htmlFor="science">
        <input value="science" onChange={(event) => setModule(event.target.value)} type="radio" name="module" id="science" />
        science
      </label>

      <button>Submit</button>
    </div>
  );
}

export default Forms;
