import React, { useState } from 'react';

function Forms() {
  const [name, setName] = useState('');
  const [age, setAge] = useState(0);
  const [city, setCity] = useState('');
  const [module, setModule] = useState('');


  const handleChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    [ name ]
  }

  return (
    <div>
      <input onChange={setName} value={name} type="text" name="name" placeholder="Nome completo" /><br /><br />

      <input onChange={handleChange} value={age} type="number" name="age" id="age"  placeholder="Idade" /><br /><br />

      <input onChange={handleChange} value={city} type="text"  name="city" placeholder="Cidade"/><br /><br />

      <label htmlFor="fundamentals">
        fundamentals
        <input onChange={handleChange} value="fundamentals" type="radio" name="module" id="fundamentals" />
      </label>

      <label htmlFor="front">
        front
        <input onChange={handleChange} value="front" type="radio" name="module" id="front" />
      </label>

      <label htmlFor="back">
        back
        <input onChange={handleChange} value="back" type="radio" name="module" id="back" />
      </label>

      <label htmlFor="science">
        science
        <input onChange={handleChange} value="science" type="radio" name="module" id="science" />
      </label>

      <button>Submit</button>
    </div>
  );
}

export default Forms;
