import React, { useState } from 'react';
import MyContext from '../contexts/MyContext';

function Forms() {
  const [name, setName] = useState('');
  const [age, setAge] = useState(0);
  const [city, setCity] = useState('');
  const [module, setModule] = useState('');


  const handleChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    [ name ]
  }

  const contextValue = {
    name,
    setName,
    age,
    setAge,
    city,
    setCity,
    module,
    setModule,
    handleChange: handleChange,
  };
  return (
    <div>
      <input onChange={handleChange} value={name} type="text" name="setName" placeholder="Nome completo" /><br /><br />

      <input onChange={handleChange} value={age} type="number" name="setAge" id="age"  placeholder="Idade" /><br /><br />

      <input onChange={handleChange} value={city} type="text"  name="setCity" placeholder="Cidade"/><br /><br />

      <label htmlFor="fundamentals">
        fundamentals
        <input onChange={handleChange} value="fundamentals" type="radio" name="module" id="fundamentals" />
      </label>

      <label htmlFor="front">
        front
        <input onChange={handleChange} value="front" type="radio" name="module" id="front" />
      </label>

      <label htmlFor="back">
        back
        <input onChange={handleChange} value="back" type="radio" name="module" id="back" />
      </label>

      <label htmlFor="science">
        science
        <input onChange={handleChange} value="science" type="radio" name="module" id="science" />
      </label>

      <button>Submit</button>
    </div>
  );
}

export default Forms;
